type Point = {
    x: number;
    y: number;
};
type Bounds = {
    minX: number;
    minY: number;
    maxX: number;
    maxY: number;
};
type Rect = {
    x: number;
    y: number;
    width: number;
    height: number;
};
type UniversalRect = {
    center: Point;
    width: number;
    height: number;
} | Bounds;

export type { Bounds, Point, Rect, UniversalRect };
