interface Point3 {
    x: number;
    y: number;
    z: number;
}
type RGBA = [number, number, number, number];
type Color = RGBA | string;
interface Box {
    center: Point3;
    size: Point3;
    color?: Color;
    rotation?: Point3;
    /** Draw the bounding box edges for debugging */
    drawBoundingBox?: boolean;
    topLabel?: string;
    topLabelColor?: Color;
    faceImages?: {
        top?: string;
    };
    projectionSubdivision?: number;
    stlUrl?: string;
    stlRotation?: Point3;
    stlPosition?: Point3;
    /** When true, fit/normalize STL mesh to the box dimensions */
    scaleStlToBox?: boolean;
    /** When true (default), center the STL/OBJ model on the box center */
    centerModel?: boolean;
    objUrl?: string;
    objRotation?: Point3;
    objPosition?: Point3;
    /** When true, fit/normalize OBJ mesh to the box dimensions */
    scaleObjToBox?: boolean;
    threeMfUrl?: string;
    threeMfRotation?: Point3;
    threeMfPosition?: Point3;
    /** When true, fit/normalize 3MF mesh to the box dimensions */
    scaleThreeMfToBox?: boolean;
}
interface Camera {
    position: Point3;
    lookAt: Point3;
    focalLength?: number;
}
interface Scene {
    boxes: Box[];
    camera: Camera;
}
interface Triangle {
    vertices: [Point3, Point3, Point3];
    normal: Point3;
    color?: Color;
}
interface STLMesh {
    triangles: Triangle[];
    boundingBox: {
        min: Point3;
        max: Point3;
    };
}

declare function renderScene(scene: Scene, opt?: {
    width?: number;
    height?: number;
    backgroundColor?: Color;
    showAxes?: boolean;
    showOrigin?: boolean;
    showGrid?: boolean;
    grid?: {
        /** world-space grid cell size (default = 1)            */ cellSize?: number;
        /** plane on which to draw the grid (default = "xz")   */ plane?: "xy" | "yz" | "xz";
    };
}): Promise<string>;

declare function loadSTL(url: string): Promise<STLMesh>;

declare function loadOBJ(url: string): Promise<STLMesh>;

declare function load3MF(url: string): Promise<STLMesh>;

export { type Box, type Camera, type Color, type Point3, type RGBA, type STLMesh, type Scene, type Triangle, load3MF, loadOBJ, loadSTL, renderScene };
